    "",
    Text(
        Sum(
            colCartItems,
            discountedPrice
        ) + Sum(
            colCartItems,
            emptyJarValue
        ),
        "[$-hi-IN]##,##,##,###",
        "hi-IN"
    )
)*/
Concatenate(
    "",
    Text(
         Sum(
            colCartItems,
            netAmount
        ),
        "[$-hi-IN]##,##,##,###",
        "hi-IN"
    )
)
